import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TodoList = () => {
  const [tasks, setTasks] = useState([]); // State to hold the list of tasks
  const [newTask, setNewTask] = useState(''); // State to hold the new task input

  // Fetch tasks from the backend when the component mounts
  useEffect(() => {
    axios.get('/api/tasks')
      .then(response => {
        setTasks(response.data); // Update state with the fetched tasks
      })
      .catch(error => {
        console.error('There was an error fetching the tasks!', error);
      });
  }, []);

  // Handle adding a new task
  const addTask = () => {
    if (newTask.trim()) { // Check if the input is not empty
      axios.post('/api/tasks', { name: newTask })
        .then(response => {
          setTasks([...tasks, response.data]); // Add the new task to the list
          setNewTask(''); // Clear the input field
        })
        .catch(error => {
          console.error('There was an error adding the task!', error);
        });
    }
  };

  // Handle deleting a task
  const deleteTask = (id) => {
    axios.delete(`/api/tasks/${id}`)
      .then(() => {
        setTasks(tasks.filter(task => task._id !== id)); // Remove the task from the list
      })
      .catch(error => {
        console.error('There was an error deleting the task!', error);
      });
  };

  return (
    <div>
      <h1>To-Do List</h1>
      <input 
        type="text" 
        value={newTask} 
        onChange={(e) => setNewTask(e.target.value)} 
        placeholder="New task" 
      />
      <button onClick={addTask}>Add Task</button>

      <ul>
        {tasks.map(task => (
          <li key={task._id}>
            {task.name}
            <button onClick={() => deleteTask(task._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TodoList;
