import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TodoList.css'; // Import the external CSS file

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [editingTaskName, setEditingTaskName] = useState('');

  useEffect(() => {
    axios.get('/api/tasks')
      .then(response => setTasks(response.data))
      .catch(error => console.error('There was an error fetching the tasks!', error));
  }, []);

  const addTask = () => {
    if (newTask.trim()) {
      axios.post('/api/tasks', { name: newTask })
        .then(response => {
          setTasks([...tasks, response.data]);
          setNewTask('');
        })
        .catch(error => console.error('There was an error adding the task!', error));
    }
  };

  const deleteTask = (id) => {
    axios.delete(`/api/tasks/${id}`)
      .then(() => setTasks(tasks.filter(task => task._id !== id)))
      .catch(error => console.error('There was an error deleting the task!', error));
  };

  const editTask = (id, name) => {
    setEditingTaskId(id);
    setEditingTaskName(name);
  };

  const updateTask = (id) => {
    if (editingTaskName.trim()) {
      axios.put(`/api/tasks/${id}`, { name: editingTaskName })
        .then(response => {
          setTasks(tasks.map(task => task._id === id ? response.data : task));
          setEditingTaskId(null);
          setEditingTaskName('');
        })
        .catch(error => console.error('There was an error updating the task!', error));
    }
  };

  return (
    <div className="card-container"> {/* Centering container */}
      <div className="card"> {/* Card container */}
        <h1>To-Do List</h1>
        <input 
          type="text" 
          value={newTask} 
          onChange={(e) => setNewTask(e.target.value)} 
          placeholder="New task" 
        />
        <button onClick={addTask}>Add Task</button>

        <ul>
          {tasks.map(task => (
            <li key={task._id}>
              {editingTaskId === task._id ? (
                <div>
                  <input 
                    type="text" 
                    value={editingTaskName} 
                    onChange={(e) => setEditingTaskName(e.target.value)} 
                  />
                  <button onClick={() => updateTask(task._id)}>Save</button>
                  <button onClick={() => setEditingTaskId(null)}>Cancel</button>
                </div>
              ) : (
                <div>
                  {task.name}
                  <button className="edit" onClick={() => editTask(task._id, task.name)}>Edit</button>
                  <button className="delete" onClick={() => deleteTask(task._id)}>Delete</button>
                </div>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default TodoList;
